import { Component } from '@angular/core';
import { share } from 'rxjs';

import { products } from '../products';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent {
  products = [...products];

  share(url: string) {
    var sharelink = "https://t.me/share/url?url="+url;
    window.open(sharelink);
  }

  onNotify() {
    window.alert('You will be notified when the product goes on sale');
  }
}
<h2> Products </h2>

<div *ngFor="let product of products" class = "products">
  <h3>
    <a [title]="product.name + ' details'" [routerLink]="['/products', product.id]">
      {{ product.name }}
    </a>
  </h3>

  <div class="img">
    <img [src]="product.img" class="images">
  </div>

  <div class="line"></div>

  <p *ngIf="product.description">
    Description: {{ product.description }}
  </p>
  <p *ngIf="product.rating">
    Rating: {{ product.rating }} ★
  </p>

  <button id="bt_share" type="button" (click)="share(product.url)">
    Share
  </button>

  <app-product-alerts
    [product]="product"
    (notify)="onNotify()">
  </app-product-alerts>
</div>
